import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  return (
    <div className="App">
      <MovieCounter></MovieCounter>
      <Nayok name = "Jashim" movies = "20" />
      <Nayok name = "Manna" movies = "25" />
      <Nayok name = "Alamgir" movies = "12" />
      <Nayok name = "Rubel" movies = "8" />
    </div>
  );
}

function Nayok(props) {
  const nayokStyle = {
    border: '2px solid purple',
    margin: '20px'
  }
  return <>
    <section>
      <div style = {nayokStyle}>
        <h1>I am : {props.name}</h1>
        <h3>I have done {props.movies} movies</h3>

      </div>
    </section>
  </>
}

// movie counter 

function MovieCounter() {
  const [count, setCount] = useState(0)
  return (
    <div>
      <button className = "btn" onClick = {() => setCount(count + 1)}>Add movie</button>
      <h5>Number of movies : {count}</h5>
      <MoveieDisplay></MoveieDisplay>
    </div>
  )
}

// movie display

function MovieDisplay() {
  return(
    <div>
      <h4>Movie I have acted : {count}</h4>
    </div>
  )
}


export default App;
