import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const [nayoks, setNayoks] = useState([])
  // const nayoks = [{name :'Jashim', age: 45},{name :'Deepjol', age : 50},{name: 'Manna', age: 60},{name :'Rubel', age : 55}]

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(res => res.json())
    .then(data => setNayoks(data))
  },[])

  return (
    <div className="App">
      <MovieCounter></MovieCounter>
      {
        nayoks.map(hero => <Nayok 
          key = {hero.id} name = {hero.name} username = {hero.username}   ></Nayok>)
      }
    </div>
  );
}

function Nayok(props) {
  const nayokStyle = {
    border: '2px solid purple',
    margin: '20px'
  }
  return <>
    <section>
      <div style = {nayokStyle}>
        <h1>I am : {props.name}</h1>
        <h2>My user name is : </h2>
        <p>My email is: </p>
        <p><b>Address: </b></p>
        <p>Phone: </p>
        <p>Website: </p>

      </div>
    </section>
  </>
}

// movie counter 

function MovieCounter() {
  const [count, setCount] = useState(0)
  return (
    <div>
      <button className = "btn" onClick = {() => setCount(count + 1)}>Add movie</button>
      <h5>Number of movies : {count}</h5>
      <MovieDisplay movies = {count}></MovieDisplay>
    </div>
  )
}

// movie display

function MovieDisplay(props) {
  return(
    <div>
      <h4>Movie I have acted : {props.movies}</h4>
    </div>
  )
}


export default App;
